package day1;

public class Practise6_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int j;
		for (i=1;i<=4;i++){
			for(j=1;j<=2*i-1;j++)			
			
			System.out.print("*");	
			System.out.println();
		}
		for (i=3;i>=1;i--){
			for(j=1;j<=2*i-1;j++)			
			
			System.out.print("*");	
			System.out.println();
		
		}
	}

}
